parameters.jsCode:
return $input.all().map(item => {
  const data = item.json.data;

  // Garante que o conteúdo seja string
  const rawHtml = String(data || '');

  // 1. Extrai conteúdo do <body> se existir
  const bodyMatch = rawHtml.match(/<body\b[^>]*>([\s\S]*?)<\/body>/i);
  const coreHtml = bodyMatch ? bodyMatch[1] : rawHtml; // Usar bodyMatch[1] para obter apenas o conteúdo dentro da tag <body>

  // 2. Remove blocos desnecessários
  const cleaned = coreHtml
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
    .replace(/<(header|nav|footer|aside|noscript|svg|form|iframe|object|canvas|template)[^>]*>[\s\S]*?<\/\1>/gi, '')
    .replace(/<!--[\s\S]*?-->/g, '')
    .replace(/<meta[^>]*>/gi, '')
    .replace(/<link[^>]*>/gi, '');

  // 3. Extrai links de imagens e descrições
  const imageMatches = [...cleaned.matchAll(/<img[^>]*src=["']([^"']+)["'](?:[^>]*alt=["']([^"']*)["'])?(?:[^>]*title=["']([^"']*)["'])?[^>]*>/gi)];
  const images = imageMatches.map(match => {
    return {
      src: match[1],
      alt: match[2] || null,
      title: match[3] || null
    };
  });

  // 4. Extrai links (href + texto)
  const linkMatches = [...cleaned.matchAll(/<a[^>]*href=["']([^"']+)["'][^>]*>(.*?)<\/a>/gi)];
  const links = linkMatches.map(match => ({
    href: match[1],
    text: match[2].replace(/<[^>]+>/g, '').trim()
  }));

  // 5. Extrai texto visível do conteúdo limpo
  const textClean = cleaned
    .replace(/<[^>]+>/g, '')            // remove qualquer tag HTML restante
    .replace(/\r\n?/g, '\n')            // normaliza quebras de linha
    .replace(/[ \t]+\n/g, '\n')         // remove espaços antes de \n
    .replace(/\s{2,}/g, ' ')            // reduz espaços múltiplos
    .replace(/\n{2,}/g, '\n')           // reduz linhas vazias
    .trim();

  // 6. Concatena tudo em uma única string
  const fullCleanedStringCliente = [
    textClean,
    '\n\n[IMAGENS]',
    ...images.map(img => `Imagem: ${img.src}\nAlt: ${img.alt || '—'}\nTitle: ${img.title || '—'}`),
    '\n\n[LINKS]',
    ...links.map(link => `Texto: ${link.text}\nURL: ${link.href}`)
  ].join('\n');

  // 7. Retorna
  return {
    json: {
      ...item.json,
      textClean,
      images,
      links,
      fullCleanedStringCliente
    }
  };
});

