// Mapa de código IBGE → sigla
const ufMap = {
  "12": "AC", "27": "AL", "13": "AM", "16": "AP", "29": "BA",
  "23": "CE", "53": "DF", "32": "ES", "52": "GO", "21": "MA",
  "31": "MG", "50": "MS", "51": "MT", "15": "PA", "25": "PB",
  "26": "PE", "22": "PI", "41": "PR", "33": "RJ", "24": "RN",
  "43": "RS", "11": "RO", "14": "RR", "42": "SC", "28": "SE",
  "35": "SP", "17": "TO"
};

// Pega todos os items recebidos
const items = $input.all();

// Extrai as UFs (convertendo código → sigla)
const ufs = items
  .map(item => ufMap[item.json.uf])
  .filter(Boolean); // remove undefined

// Remove duplicados
const ufsUnicas = [...new Set(ufs)];

// Retorna em um único item JSON
return [
  {
    json: {
      ufs_unicas: ufsUnicas
    }
  }
];

// --- Entrada --------------------------------------------------
const input = $input.first().json.body?.parametros?.cnpjs_existentes || "";

// --- Captura todos os CNPJs, formatados ou não ----------------
// Pega qualquer padrão de 14 dígitos, com ou sem pontos, barras, traços
const candidatos = (input.match(/\d{2}\.?\d{3}\.?\d{3}\/?\d{4}-?\d{2}/g) || [])
  .map(c => c.replace(/\D/g, '')); // remove tudo que não é dígito

// Caso também existam CNPJs sem formatação misturados no texto (14 dígitos corridos)
const candidatosExtras = (input.match(/\b\d{14}\b/g) || [])
  .map(c => c.replace(/\D/g, ''));

// Une e remove duplicados
const todos = [...new Set([...candidatos, ...candidatosExtras])];

// --- Função de validação do CNPJ -------------------------------
function validarCNPJ(cnpj) {
  if (!cnpj || cnpj.length !== 14) return false;
  if (/^(\d)\1+$/.test(cnpj)) return false; // ignora repetidos

  let tamanho = cnpj.length - 2;
  let numeros = cnpj.substring(0, tamanho);
  let digitos = cnpj.substring(tamanho);
  let soma = 0;
  let pos = tamanho - 7;

  for (let i = tamanho; i >= 1; i--) {
    soma += numeros.charAt(tamanho - i) * pos--;
    if (pos < 2) pos = 9;
  }

  let resultado = soma % 11 < 2 ? 0 : 11 - (soma % 11);
  if (resultado != digitos.charAt(0)) return false;

  tamanho++;
  numeros = cnpj.substring(0, tamanho);
  soma = 0;
  pos = tamanho - 7;

  for (let i = tamanho; i >= 1; i--) {
    soma += numeros.charAt(tamanho - i) * pos--;
    if (pos < 2) pos = 9;
  }

  resultado = soma % 11 < 2 ? 0 : 11 - (soma % 11);
  return resultado == digitos.charAt(1);
}

// --- Formata o CNPJ --------------------------------------------
function formatCNPJ(cnpj) {
  return cnpj.replace(/^(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})$/, '$1.$2.$3/$4-$5');
}

// --- Processa, valida e retorna --------------------------------
const cnpjsValidos = todos
  .filter(validarCNPJ)
  .map(formatCNPJ);

return [
  {
    cnpjs_formatados: cnpjsValidos
  }
];

