function cleanStr(v) {
  if (v === null || v === undefined) return "";
  const s = String(v).trim();
  if (!s || s.toLowerCase() === "null" || s.toLowerCase() === "undefined") return "";
  return s;
}

function uniq(arr) {
  return Array.from(new Set((arr || []).map(cleanStr).filter(Boolean)));
}

function joinLines(lines) {
  return uniq(lines).join("\n");
}

function formatList(title, items) {
  const list = uniq(items);
  if (!list.length) return "";
  return `${title}:\n- ${list.join("\n- ")}`;
}

function flattenProdutos(produtosArr) {
  const out = [];
  for (const p of (produtosArr || [])) {
    const cat = cleanStr(p?.categoria);
    const prods = uniq(p?.produtos || []);
    if (cat && prods.length) out.push(`${cat}: ${prods.join(", ")}`);
    else if (cat) out.push(cat);
    else if (prods.length) out.push(prods.join(", "));
  }
  return out;
}

function flattenServicos(servicosArr) {
  const out = [];
  for (const s of (servicosArr || [])) {
    const nome = cleanStr(s?.nome);
    const desc = cleanStr(s?.descricao);
    if (nome && desc) out.push(`${nome}: ${desc}`);
    else if (nome) out.push(nome);
    else if (desc) out.push(desc);
  }
  return out;
}

function normalizeCNPJ(cnpj) {
  const digits = cleanStr(cnpj).replace(/\D/g, "");
  return digits || null;
}

function choosePointId(input) {
  const cnpj = normalizeCNPJ(input?.perfil_gemini?.identidade?.cnpj);
  return cnpj || cleanStr(input?.id) || cryptoRandomUUIDFallback();
}

function cryptoRandomUUIDFallback() {
  const rnd = () => Math.floor(Math.random() * 0xffffffff).toString(16).padStart(8, "0");
  return `${rnd().slice(0, 8)}-${rnd().slice(0, 4)}-${rnd().slice(0, 4)}-${rnd().slice(0, 4)}-${rnd()}`;
}

function buildTexts(pg) {
  const identidade = pg?.identidade || {};
  const classificacao = pg?.classificacao || {};
  const ofertas = pg?.ofertas || {};
  const reputacao = pg?.reputacao || {};
  const contato = pg?.contato || {};

