  ].filter(Boolean);

  const perfil_text = joinLines([
    `Empresa: ${cleanStr(identidade?.nome_empresa)}`,
    cleanStr(identidade?.descricao) ? `Descrição: ${cleanStr(identidade?.descricao)}` : "",
    cleanStr(classificacao?.industria) ? `Indústria: ${cleanStr(classificacao?.industria)}` : "",
    cleanStr(classificacao?.modelo_negocio) ? `Modelo de negócio: ${cleanStr(classificacao?.modelo_negocio)}` : "",
    cleanStr(classificacao?.cobertura_geografica) ? `Cobertura geográfica: ${cleanStr(classificacao?.cobertura_geografica)}` : "",
    produtosTxt.length ? formatList("Produtos", produtosTxt) : "",
    servicosTxt.length ? formatList("Serviços", servicosTxt) : "",
    reputacaoTxt.length ? reputacaoTxt.join("\n") : "",
    cleanStr(contato?.url_site) ? `Site: ${cleanStr(contato?.url_site)}` : "",
  ]);

  const segmento_text = joinLines([
    cleanStr(classificacao?.industria) ? `Indústria: ${cleanStr(classificacao?.industria)}` : "",
    cleanStr(classificacao?.modelo_negocio) ? `Modelo de negócio: ${cleanStr(classificacao?.modelo_negocio)}` : "",
    cleanStr(classificacao?.publico_alvo) ? `Público-alvo: ${cleanStr(classificacao?.publico_alvo)}` : "",
    cleanStr(classificacao?.cobertura_geografica) ? `Cobertura geográfica: ${cleanStr(classificacao?.cobertura_geografica)}` : "",
  ]);

  const clientesList = uniq(reputacao?.lista_clientes);
  const parceriasList = uniq(reputacao?.parcerias);

  const clientes_text = joinLines([
    clientesList.length ? formatList("Lista de clientes", clientesList) : "",
    parceriasList.length ? formatList("Parcerias", parceriasList) : "",
  ]);

  return { perfil_text, segmento_text, clientes_text };
}

