const input = $input.first().json;

const vectorWeights = input.output?.vector_weights || {};
const payloadFilters = input.output?.payload_filters || { must: [], must_not: [], should: [] };
const geoFilter = input.output?.geographic_filter || {};
const vectorQueries = input.vector_queries || {};


// =============================
// PARSE VECTOR SAFE
// =============================

function parseVector(v) {
  if (!v) return null;

  if (Array.isArray(v)) return v;

  if (typeof v === "string") {
    try {
      return JSON.parse(v);
    } catch (e) {
      return null;
    }
  }

  return null;
}


// =============================
// BUILD FILTER (QDRANT FORMAT)
// =============================

const must = [];
const must_not = [];
const should = [];

// payload MUST
for (const f of payloadFilters.must || []) {
  if (f?.field && f?.value !== undefined) {
    must.push({
      key: f.field,
      match: { value: f.value }
    });
  }
}

// payload MUST NOT
for (const f of payloadFilters.must_not || []) {
  if (f?.field && f?.value !== undefined) {
    must_not.push({
      key: f.field,
      match: { value: f.value }
    });
  }
}

// payload SHOULD (OR lógico)
for (const f of payloadFilters.should || []) {
  if (f?.field && f?.value !== undefined) {
    should.push({
      key: f.field,
      match: { value: f.value }
    });
  }
}


// =============================
// GEO FILTER
// =============================

if (Array.isArray(geoFilter.ufs) && geoFilter.ufs.length) {
  must.push({
    key: "uf",
    match: { any: geoFilter.ufs }
  });
}

if (Array.isArray(geoFilter.cidades) && geoFilter.cidades.length) {
  must.push({
    key: "cidade",
    match: { any: geoFilter.cidades }
  });
}


// =============================
// FINAL FILTER OBJECT
// =============================

const filter = {};

if (must.length) filter.must = must;
if (should.length) filter.should = should;
if (must_not.length) filter.must_not = must_not;

const hasFilter = Object.keys(filter).length > 0;


// =============================
// BUILD SEARCHES (MULTI VECTOR)
// =============================

const searches = [];

for (const vectorName in vectorQueries) {

  const vector = parseVector(vectorQueries[vectorName]);
  if (!vector) continue;

  searches.push({
    using: `v_${vectorName}`,     // nome exato do vetor na collection
    vector: vector,
    limit: 10,
    with_payload: true,
    ...(hasFilter && { filter })
  });
}


// =============================
// RETORNO FINAL (HTTP BODY)
// =============================

return [
  {
    json: {
      searches
    }
  }
];
